import React, { useEffect, useState } from "react";
import sun from './sun.png';
import './App.css';

let pi = 3.14;
let radius = 696340;

function App() {

  const [latestPiValue, setPiValue] = useState("");

  // This method fetches the records from the database.
  useEffect(() => {
    async function getPiValue() {
      const response = await fetch(`https://pi-challenge.herokuapp.com/pi-challenge`);

      if (!response.ok) {
        const message = `An error occurred: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const latestPiValue = await response.json();
      console.log(latestPiValue);
      setPiValue(latestPiValue);
    }

    getPiValue();

    return;
  });

  return (
    <div className="App">
      <header className="App-header">
        <img src={sun} alt="logo" />
        <p>
          Current known value of Pi : {latestPiValue}
        </p>
        <p>
          The circumference of the Sun: {2*pi*radius} km
        </p>
      </header>
    </div>
  );
}

export default App;
